INSERT INTO `game_vault` (`id`, `title`, `time`, `top_id`, `bottom_id`, `left_id`, `right_id`, `text`, `heal`) VALUES
(208, 'Оружейный Зал', 1, 0, 207, 0, 0, 'Вы проходите в небольшой грот, который называется <B>Оружейным Залом</B>, и замираете в восхищении.<BR>\r\nНеизвестный древний каменотес выгравировал на стенах пещеры великолепное оружие, подобного которому Вам еще не приходилось видеть.<BR>\r\nРассматривая идеальные гравировки на стенах, между ними Вы замечаете лаконичную надпись: <I>«Назад - в <B>Зал Призраков №2</B>»</I>.', 1238160878),
(207, 'Зал Призраков №2', 1, 208, 206, 201, 0, 'Леденящий кожу стон ветра. Капание воды. Режущие ноги сталагмиты. <B>Зал Призраков №2</B>.<BR>\r\nУжасные бестелесные тени на стенах пещеры. Они движутся, и через некоторое время Вам начинает казаться, что Вы слышите их зловещие голоса. Вас по-прежнему не покидает чувство тревоги, и Вы ощущаете, как оно растет.<BR>\r\nВглядываясь в неровности стен, Вы находите надпись: <I>«Повернув назад, Вы попадете в ужас <B>Зала Хаоса</B>. Пройдя через узкий проход налево, окажетесь в <B>Большом Коридоре</B>. А если не побоитесь и пройдете вперед - то попадете в <B>Оружейный Зал</B>»</I>.', 1238395358),
(205, 'Тупиковый Тоннель', 1, 0, 204, 0, 0, 'Осторожно ступая по сужающемуся тоннелю, Вы неожиданно наталкиваетесь на сплошную стену, выскочившую будто из неоткуда.<BR>\r\nНадпись на стене гласит: <I>«Это тупик. Возвращайтесь назад, и да помогут Вам Боги»</I>.', 1238319686),
(206, 'Зал Хаоса', 1, 207, 0, 0, 0, 'Очутившись в <B>Зале Хаоса</B>, Вы неожиданно начинаете испытывать тревогу. Она растет, превращаясь в панику!<BR>\r\nСвист ветра, капание воды и непонятно откуда льющийся свет кажутся Вам порождением Сатаны. В панике Вы шарите руками по стенам и натыкаетесь на надпись: <I>«Посмотрев назад Вы окажетесь в <B>Зале Странствий</B>, продвинувшись вперед, попадете в <B>Зал Призраков №2</B>»</I>.<BR>\r\nНе до конца осознав смысл прочитанного, Вы уже бежите по коридору в надежде оказаться как можно дальше от этой проклятой пещеры.', 1238580560),
(203, 'Зал Призраков', 1, 204, 0, 0, 202, 'Таинственный <B>Зал Призраков №1</B>.<BR>\r\nЗдесь всегда заунывно стонет ветер. Вы чувствуете озноб, но не понимаете, от чего, от страха или холода. То и дело Вам кажется, что свет, обретая форму покинувших наш мир душ, зовет вас в свой мир, мир теней. Вы в отчаянии ищете на стене очередной указатель…<BR>\r\nЕсть! <I>«Еще не поздно вернуться назад в <B>Зал Странствий</B>. А пройдя по пещере вперед, Вы окажетесь в <B>Зале Бездны</B>»</I>.', 1238318489),
(204, 'Зал Бездны', 1, 205, 203, 0, 201, 'Оказавшись в <B>Зале Бездны</B>, вы не верите своим глазам.<BR>\r\nПо центру огромной пещеры зловеще открыла бездонную пасть воронка, лишь немного уступающая пещере по площади. Осторожно переступая вдоль стены Вы ищете указатель.<BR>\r\nОбойдя почти всю пещеру по периметру, наконец обнаруживаете его: <I>«Три пути есть у Вас из этой пещеры. Пойти вниз в <B>Зал Призраков</B>, свернуть вправо, в <B>Большой Коридор</B> или же по тоннелю вверх в <B>Тупиковый Тоннель</B>»</I>.', 1238319133),
(200, 'Врата Подземелья', 10, 0, 201, 0, 0, 'Вы входите во <B>Врата Подземелья</B>…<BR>\r\nЭто древняя пещера, из которой веет смертью. Если Вы все же осмелитесь войти, то попадете в <B>Большой Коридор</B>.', 1177070720),
(201, 'Большой Коридор', 90, 200, 202, 0, 207, 'Вы находитесь в <B>Большом Коридоре</B>.<BR>\r\nЭто огромный грот, стены которого устрашающе нависают над Вами… Вам холодно и страшно.<BR>\r\nНа одной из стен неизвестным существом высечены слова: <I>«Вы можете вернуться назад в <B>Большой Коридор</B>. Пройдя вперед, Вы попадете в <B>Зал Странствий</B>. А если свернете направо, то окажетесь в <B>Зале Призраков №2</B>»</I>.', 1238590021),
(202, 'Зал Странствий', 1, 201, 303, 203, 206, 'Войдя в <B>Зал странствий</B>, Вы оказываетесь в круглой невысокой пещере, с потолка которой на Вас угрожающе смотрят острия сотен сталактитов. Слышится капание воды…<BR>\r\nИ здесь на стене проводник оставил послание для заблудившихся путников: <I>«Повернувшись назад, Вы попадете в <B>Большой Коридор</B>. Левый проход выведет вас в <B>Зал Призраков №1</B>, а правый - в <B>Зал Хаоса</B>»</I>.', 1238580394),
(303, 'Сектор 3', 1, 202, 306, 0, 0, 'Вы входите в <B>Лабиринт смерти</B>…<BR>\r\nЭто древний лабиринт, из которой веет смертью.', 1238583120),
